package com.entity;

public class Specialist {

private int id;
private String Name;



public Specialist() {
	super();
	// TODO Auto-generated constructor stub
}

public Specialist(int id, String name) {
	super();
	this.id = id;
	Name = name;
}

public int getId() {
	return id;
}
public void setId(int id) {
	this.id = id;
}
public String getName() {
	return Name;
}
public void setName(String name) {
	Name = name;
}

	
}
